$(function(){
    $('.slider').slick({
        prevArrow:'<button type="button" class="slick-arrows slick-prev"><img src="img/chevron-left.svg"></button>',
        nextArrow: '<button type="button" class="slick-arrows slick-next"><img src="img/chevron-right.svg"></button>',
        autoplay:true,
        autoplaySpeed:5000,
        fade: true,
        pauseOnHover:false
    });
    $('.goods__slider').slick({
        prevArrow: '<button type="button" class="slick-arrows slick-prev"><img src="img/chevron-left.svg"></button>',
        nextArrow: '<button type="button" class="slick-arrows slick-next"><img src="img/chevron-right.svg"></button>',
        autoplay: true,
        autoplaySpeed: 5000,
        slidesToShow: 4,
        slidesToScroll: 1,
        pauseOnHover:false,
        responsive: [
            {
                breakpoint: 1150,
                settings: {
                    slidesToShow: 3,
                }
            },
            {
                breakpoint: 900,
                settings: {
                    slidesToShow: 2,
                }
            },
            {
                breakpoint: 630,
                settings: {
                    slidesToShow: 1,
                }
            }
        ]
    });
    $('.menu__btn').on('click', function(){
        $('.menu__list').slideToggle('3000')
    });
    $('.drop-scanners').on('click', function () {
        $('.dropdown__scanners').slideToggle('3000')
    });
    $('.drop-elm327').on('click', function () {
        $('.dropdown__elm327').slideToggle('3000')
    });
});